mapper.select:
SELECT



FROM yisheng  a



        and a.id in

        #{item}



        and a.username like CONCAT('%',#{params.username},'%')


        and a.password like CONCAT('%',#{params.password},'%')


        and a.yisheng_name like CONCAT('%',#{params.yishengName},'%')


        and a.yisheng_phone like CONCAT('%',#{params.yishengPhone},'%')


        and a.sex_types = #{params.sexTypes}


        and a.yisheng_email like CONCAT('%',#{params.yishengEmail},'%')


        and a.yisheng_types = #{params.yishengTypes}


          and a.yisheng_number >= #{params.yishengNumberStart}   


          and a.yisheng_number <= #{params.yishengNumberEnd}   


        and a.yisheng_number = #{params.yishengNumber}


          and a.yisheng_newMoney >= #{params.yishengNewmoneyStart}   


          and a.yisheng_newMoney <= #{params.yishengNewmoneyEnd}   


          and UNIX_TIMESTAMP(a.yisheng_time) >= UNIX_TIMESTAMP(#{params.yishengTimeStart}) 


          and UNIX_TIMESTAMP(a.yisheng_time) <= UNIX_TIMESTAMP(#{params.yishengTimeEnd}) 


        and a.yisheng_zhicheng like CONCAT('%',#{params.yishengZhicheng},'%')


        and a.yisheng_content like CONCAT('%',#{params.yishengContent},'%')


          and a.yisheng_delete >= #{params.yishengDeleteStart}   


          and a.yisheng_delete <= #{params.yishengDeleteEnd}   


        and a.yisheng_delete = #{params.yishengDelete}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



order by a.${params.sort} ${params.order}
